import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Line } from 'react-chartjs-2';
import { CategoryScale } from 'chart.js';

const MonthlySalesGraph = () => {
  const { vendorId } = useParams(); // Get the vendor ID from the URL
  const [salesData, setSalesData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`/orders/products/monthly/${vendorId}`)
      .then((response) => response.json())
      .then((data) => {
        setSalesData(data.data.salesData);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, [vendorId]);

  // Process the sales data to get monthly totals
  const monthlySales = getMonthlySales(salesData);

  // Render the table
  return (
    <div>
      <h2>Total Sales for Vendor: {vendorId}</h2>
      {loading ? (
        <p>Loading data...</p>
      ) : (
        <div>
          <table>
            <thead>
              <tr>
                <th>Product Name</th>
                <th>Total Amount Sold</th>
              </tr>
            </thead>
            <tbody>
              {salesData.map((product) => (
                <tr key={product._id}>
                  <td>{product.productName}</td>
                  <td>{product.totalSold}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <LineChart monthlySales={monthlySales} />
        </div>
      )}
    </div>
  );
}

// Function to process sales data and calculate monthly totals
function getMonthlySales(data) {
  const monthlySales = new Array(12).fill(0);

  data.forEach((product) => {
    // You might need to parse the date from the product and extract the month
    // For example: const month = new Date(product.date).getMonth();
    // You can adjust this based on your actual data structure
    // Here, we're using a placeholder value of 0 for the month
    const month = 0;

    monthlySales[month] += product.totalSold;
  });

  return monthlySales;
}

// Component to render the line chart
const LineChart = ({ monthlySales }) => {
  const data = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'],
    datasets: [
      {
        label: 'Monthly Sales',
        data: monthlySales,
        fill: false,
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1,
      },
    ],
  };

  return (
    <div>
      <h2>Monthly Sales Chart</h2>
      <Line data={data} />
    </div>
  );
}

export default MonthlySalesGraph;
