import React, { useEffect, useState } from 'react';

const TotalSalesTable = ({ match }) => {
  const vendorId = match.params.vendorId; // Get the vendor ID from the URL
  const [salesData, setSalesData] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:8000/orders/products/${vendorId}`)
      .then((response) => response.json())
      .then((data) => setSalesData(data));
  }, [vendorId]);

  // Render the table
  return (
    <div>
      <h2>Total Sales for Vendor: {vendorId}</h2>
      <table>
        <thead>
          <tr>
            <th>Product Name</th>
            <th>Total Amount Sold</th>
          </tr>
        </thead>
        <tbody>
          {salesData.map((item) => (
            <tr key={item.productName}>
              <td>{item.productName}</td>
              <td>{item.totalAmountSold}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default TotalSalesTable;
