import {React, useState, useEffect} from 'react';
import { Line } from 'react-chartjs-2';
import LineChart from './LineChart';
import { UserData } from './Data';
import { useParams } from 'react-router-dom';



function MonthlySalesGraph() {
  const {vendorId} = useParams();
  const [monthlyData, setMonthlyData]= useState([]);
  const [loading,setLoading] =useState(true);
  

  useEffect(() => {
    fetch(`/orders/products/monthly/${vendorId}`)
      .then((response) => response.json())
      .then((data) => {
        // Process the API response and prepare the chart data
        const paymentData = data.data.paymentDate;
        const monthlyTotals = {};

        paymentData.forEach(product => {
          product.paymentDate.forEach(month => {
            for (const [key, value] of Object.entries(month)) {
              if (monthlyTotals[key]) {
                monthlyTotals[key] += value;
              } else {
                monthlyTotals[key] = value;
              }
            }
          });
        });

        const updatedChartData = {
          labels: Object.keys(monthlyTotals),
          datasets: [
            {
              label: 'Total Sold',
              data: Object.values(monthlyTotals),
              fill: false,
              borderColor: 'rgba(75, 192, 192, 1)',
            },
          ],
        };

        setChartData(updatedChartData);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, [vendorId]);

  function getMonthlySales(data) {
    const monthlyTotals = {};
    
    data.forEach((item) => {
      if (item.paymentDate) {
        Object.entries(item.paymentDate).forEach(([month, total]) => {
          if (monthlyTotals[month]) {
            // If the month already exists in the totals, add the new total to it
            monthlyTotals[month] += total;
          } else {
            // If the month doesn't exist, create it with the new total
            monthlyTotals[month] = total;
          }
        });
      }
    });
    
    return monthlyTotals;
  }

  // IF YOU SEE THIS COMMENT: I HAVE GOOD EYESIGHT

  return (
    <div className="App">
      <h2>Total Sales for Vendor: {vendorId}</h2>
      {loading ? (
        <p>Loading data...</p>
      ) : (
        <div style={{ width: 700 }}>
          <table>
            {/* Display the product table */}
          </table>
          <LineChart monthlyTotals={getMonthlySales(monthlyData)} />
        </div>
      )}

    </div>
  );
}

export default MonthlySalesGraph;
