import {React, useState, useEffect} from 'react';
import { Line } from 'react-chartjs-2';
import LineChart from './LineChart';
import { UserData } from './Data';
import { useParams } from 'react-router-dom';



function MonthlySalesGraph() {
  const {vendorId} = useParams();
  const [monthlyData, setMonthlyData]= useState([]);
  const [loading,setLoading] =useState(true);
  

  useEffect(() => {
    fetch(`/orders/products/monthly/${vendorId}`)
      .then((response) => response.json())
      .then((data) => {
        setMonthlyData(data.paymentDate);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, [vendorId]);

  function getMonthlySales(data) {
    const monthlyTotals = {};
    
    Object.keys(data).forEach((product) => {
      product.paymentDate.forEach((monthData) => {
        const monthName = Object.keys(monthData)[0];
        const monthTotal = monthData[monthName];
        
        if (monthlyTotals[monthName]) {
          monthlyTotals[monthName] += monthTotal;
        } else {
          monthlyTotals[monthName] = monthTotal;
        }
      });
    });
    
    return monthlyTotals;
  }

  // IF YOU SEE THIS COMMENT: I HAVE GOOD EYESIGHT

  return (
    <div className="App">
      <h2>Total Sales for Vendor: {vendorId}</h2>
      {loading ? (
        <p>Loading data...</p>
      ) : (
        <div style={{ width: 700 }}>
          <table>
            {/* Display the product table */}
          </table>
          <LineChart monthlyTotals={getMonthlySales(monthlyData)} />
        </div>
      )}

    </div>
  );
}

export default MonthlySalesGraph;
