import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const TotalSalesTable = () => {
  const { vendorId } = useParams(); // Get the vendor ID from the URL
  const [salesData, setSalesData] = useState([]);

  useEffect(() => {
    fetch(`/orders/products/${vendorId}`)
      .then((response) => response.json())
      .then((data) => setSalesData(data));
  }, [vendorId]);

  // Render the table
  return (
    <div>
      <h2>Total Sales for Vendor: {vendorId}</h2>
      <table>
        <thead>
          <tr>
            <th>Product Name</th>
            <th>Total Amount Sold</th>
          </tr>
        </thead>
        <tbody>
        {Object.entries(salesData).map(([productId, productData]) => (
    <tr key={productId}>
      <td>{productData.productName}</td>
      <td>{productData.totalSold}</td>
    </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default TotalSalesTable;
