import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';


import {
    Chart as ChartJS,
    LineElement,
    CategoryScale,
    LinearScale,
    PointElement
} from 'chart.js';

ChartJS.register(
    LineElement,
    CategoryScale,
    LinearScale,
    PointElement
)
const MonthlySalesGraph = () => {
    const { vendorId } = useParams(); // Get the vendor ID from the URL
    const [salesData, setSalesData] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetch(`/orders/products/monthly/${vendorId}`)
            .then((response) => response.json())
            .then((data) => {
                setSalesData(data.data.salesData);
                setLoading(false);
            })
            .catch((error) => {
                console.error('Error fetching data:', error);
            });
    }, [vendorId]);

    // Process the sales data to get monthly totals
    const monthlySales = getMonthlySales(salesData);

    // Render the table
    return (
        <div>
            <h2>Total Sales for Vendor: {vendorId}</h2>
            {loading ? (
                <p>Loading data...</p>
            ) : (
                <div>
                    <table>
                        <thead>
                            <tr>
                                <th>Product Name</th>
                                <th>Total Amount Sold</th>
                            </tr>
                        </thead>
                        <tbody>
                            {salesData.map((product) => (
                                <tr key={product._id}>
                                    <td>{product.productName}</td>
                                    <td>{product.totalSold}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    <LineChart monthlySales={monthlySales} />
                </div>
            )}
        </div>
    );
};

// Function to process sales data and calculate monthly totals
function getMonthlySales(data) {
    const monthlySales = new Array(12).fill(0);

    data.forEach((product) => {
        product.paymentDate.forEach((monthData) => {
            // Loop through each month in the product's paymentDate
            // Assuming monthData is an object like { "Şubat": 6 }
            // You may need to map Turkish month names to month numbers
            const monthName = Object.keys(monthData)[0];
            const monthTotal = monthData[monthName];
      
            // Map Turkish month names to month numbers (adjust as needed)
            const monthMap = {
              Ocak: 0,
              Şubat: 1,
              Mart: 2,
              Nisan: 3,
              Mayıs: 4,
              Haziran: 5,
              Temmuz: 6,
              Ağustos: 7,
              Eylül: 8,
              Ekim: 9,
              Kasım: 10,
              Aralık: 11,
            };
      
            const month = monthMap[monthName];
      
            if (month !== undefined) {
              monthlySales[month] += monthTotal;
            }
          });
        
    });

    return monthlySales;
}

// Component to render the line chart
const LineChart = ({ monthlyTotals }) => {
    const data = {
      labels: Object.keys(monthlyTotals),
      datasets: [
        {
          label: 'Monthly Sales',
          data: Object.values(monthlyTotals),
          fill: false,
          borderColor: 'rgb(75, 192, 192)',
          tension: 0.1,
        },
      ],
    };
  
    return (
      <div>
        <h2>Monthly Sales Chart</h2>
        <Line data={data} />
      </div>
    );
  };

export default MonthlySalesGraph;
